mapper.select:

select 

,

from nk_doc_def_i
where CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_TYPE = #{docType,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=INTEGER}

mapper.delete:

delete from nk_doc_def_i
where CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_TYPE = #{docType,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=INTEGER}

mapper.update:

update nk_doc_def_i
set CARD_HANDLER = #{cardHandler,jdbcType=VARCHAR},
  CARD_NAME = #{cardName,jdbcType=VARCHAR},
  ORDER_BY = #{orderBy,jdbcType=INTEGER},
  CALC_ORDER = #{calcOrder,jdbcType=INTEGER},
  CALC_TIMES = #{calcTimes,jdbcType=INTEGER},
  EDITABLE_SP_E_L = #{editableSpEL,jdbcType=VARCHAR},
  MARKDOWN_FLAG = #{markdownFlag,jdbcType=INTEGER},
  UPDATED_TIME = #{updatedTime,jdbcType=BIGINT}
where CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_TYPE = #{docType,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=INTEGER}
