mapper.select:

select 

,

from nk_card_def_h
where COMPONENT_NAME = #{componentName,jdbcType=VARCHAR}
  and COMPONENT_VERSION = #{componentVersion,jdbcType=INTEGER}

mapper.delete:

delete from nk_card_def_h
where COMPONENT_NAME = #{componentName,jdbcType=VARCHAR}
  and COMPONENT_VERSION = #{componentVersion,jdbcType=INTEGER}

mapper.update:

update nk_card_def_h
set COMPONENT_ACTIVE = #{componentActive,jdbcType=INTEGER},
  COMPONENT_DESC = #{componentDesc,jdbcType=VARCHAR},
  OWNER = #{owner,jdbcType=VARCHAR},
  CREATED_TIME = #{createdTime,jdbcType=BIGINT},
  UPDATED_TIME = #{updatedTime,jdbcType=BIGINT}
where COMPONENT_NAME = #{componentName,jdbcType=VARCHAR}
  and COMPONENT_VERSION = #{componentVersion,jdbcType=INTEGER}
